mapper.select:

select

from rcs_tournament_template_play_margain_ref
where id = #{id,jdbcType=INTEGER}

    	SELECT distinct m.market_category_id category,odds_type oddType FROM standard_sport_market_odds o 
		LEFT JOIN standard_sport_market m ON m.id = o.market_id
		WHERE m.standard_match_info_id = #{matchId} AND m.market_category_id = #{playId}

    	SELECT r.* FROM rcs_tournament_template t
		LEFT JOIN rcs_tournament_template_play_margain m ON m.template_id = t.id
		LEFT JOIN rcs_tournament_template_play_margain_ref r ON r.id = m.valid_margin_id
		WHERE m.valid_margin_id IS NOT NULL AND t.`type` = 3 AND t.type_val = #{matchId}
		AND t.match_type = #{matchType} AND m.play_id = #{playId}

    	SELECT m.* FROM rcs_tournament_template t
		LEFT JOIN rcs_tournament_template_play_margain m ON m.template_id = t.id
		WHERE m.valid_margin_id IS NOT NULL AND t.`type` = 3 AND t.type_val = #{matchId}
		AND t.match_type = #{matchType} AND m.play_id = #{playId}

SELECT * FROM (SELECT t1.id match_id,t1.match_type,t1.pause_wait_time,t1.pause_margain,t1.normal_wait_time,t1.market_count,t1.vice_market_ratio,t1.t_id template_id,t1.m_id margin_id,t1.play_id,t1.time_val,r3.id margin_ref_id,m1.valid_margin_id,1 have_handicap FROM ( SELECT id,t_id,m_id,t.match_type,t.market_count,t.pause_wait_time,t.pause_margain,t.normal_wait_time,t.vice_market_ratio,t.play_id,case when match_type = 0 then MAX(time_val) else MIN(time_val) end time_val FROM ( SELECT i.id , t.id t_id ,m.id m_id,m.play_id,r.id m_r_id,r.time_val ,t.match_type,m.market_count,m.vice_market_ratio,r.pause_wait_time,r.pause_margain,r.normal_wait_time FROM standard_match_info i LEFT JOIN rcs_tournament_template t ON t.`type` = 3 AND t.type_val = i.id LEFT JOIN rcs_tournament_template_play_margain m ON m.template_id = t.id LEFT JOIN rcs_tournament_template_play_margain_ref r ON r.margain_id = m.id LEFT JOIN standard_sport_market_category_ref cr ON cr.category_id = m.play_id AND cr.sport_id = i.sport_id WHERE i.begin_time > unix_timestamp(now()) * 1000 - 1000 * 60 * 60 * 12 AND i.sport_id = 2 AND i.match_status NOT IN (3,4) AND r.`status` NOT IN (2) AND m.match_type = 1 AND i.odds_live = 0 AND i.match_period_id = 0 AND r.time_val > (i.begin_time - unix_timestamp(now()) * 1000)/1000
AND i.id in
#{matchId}
) t GROUP BY id,t_id,m_id,t.play_id,match_type ) t1 LEFT JOIN rcs_tournament_template_play_margain_ref r3 ON r3.margain_id = t1.m_id AND r3.time_val = t1.time_val LEFT JOIN rcs_tournament_template_play_margain m1 ON m1.id = t1.m_id
WHERE (m1.valid_margin_id <> r3.id OR m1.valid_margin_id IS NULL or r3.`status` = 3)
AND (SELECT count(1) FROM standard_sport_market k left JOIN standard_sport_market_odds o ON o.market_id = k.id WHERE k.standard_match_info_id = t1.id AND k.market_category_id = t1.play_id AND o.id IS NOT NULL and k.third_market_source_status in (0,1) ) > 0 UNION ALL SELECT t1.id match_id,t1.match_type,t1.pause_wait_time,t1.pause_margain,t1.normal_wait_time,t1.market_count,t1.vice_market_ratio,t1.t_id template_id,t1.m_id margin_id,t1.play_id,t1.time_val,r3.id margin_ref_id,m1.valid_margin_id,0 have_handicap FROM ( SELECT id,t_id,m_id,t.match_type,t.market_count,t.pause_wait_time,t.pause_margain,t.normal_wait_time,t.vice_market_ratio,t.play_id,case when match_type = 0 then MAX(time_val) else MIN(time_val) end time_val FROM ( SELECT i.id , t.id t_id ,m.id m_id,m.play_id,r.id m_r_id,r.time_val ,t.match_type,m.market_count,m.vice_market_ratio,r.pause_wait_time,r.pause_margain,r.normal_wait_time FROM standard_match_info i LEFT JOIN rcs_tournament_template t ON t.`type` = 3 AND t.type_val = i.id LEFT JOIN rcs_tournament_template_play_margain m ON m.template_id = t.id LEFT JOIN rcs_tournament_template_play_margain_ref r ON r.margain_id = m.id LEFT JOIN standard_sport_market_category_ref cr ON cr.category_id = m.play_id AND cr.sport_id = i.sport_id WHERE i.begin_time > unix_timestamp(now()) * 1000 - 1000 * 60 * 60 * 12 AND i.sport_id = 2 AND i.match_status NOT IN (3,4) AND r.`status` NOT IN (2) AND m.match_type = 1 AND i.odds_live = 0 AND i.match_period_id = 0 AND r.time_val > (i.begin_time - unix_timestamp(now()) * 1000)/1000
AND i.id in
#{matchId}
) t GROUP BY id,t_id,m_id,t.play_id,match_type ) t1 LEFT JOIN rcs_tournament_template_play_margain_ref r3 ON r3.margain_id = t1.m_id AND r3.time_val = t1.time_val LEFT JOIN rcs_tournament_template_play_margain m1 ON m1.id = t1.m_id WHERE r3.`status` = 3 AND m1.market_count IS NULL AND (SELECT count(1) FROM standard_sport_market k left JOIN standard_sport_market_odds o ON o.market_id = k.id WHERE k.standard_match_info_id = t1.id AND k.market_category_id = t1.play_id AND o.id IS NOT NULL and k.third_market_source_status in (0,1) ) = 0 ) t1
