mapper.select:
SELECT
rss.match_info_id
FROM
rcs_standard_sport_market_sell rss
LEFT JOIN standard_match_info smi ON smi.id = rss.match_info_id
WHERE
is_delete = 0
AND smi.match_status NOT IN ( 3 )
AND smi.begin_time > ( UNIX_TIMESTAMP( NOW())- 24 * 60 * 60 * 3 )* 1000


    AND rss.pre_trader_id = #{co.userId}



    AND rss.live_trader_id = #{co.userId}


    and rss.begin_time > #{co.beginTime}


    and rss.sport_id = #{co.sportId}

mapper.update:
UPDATE rcs_standard_sport_market_sell

    SET pre_trader_id=#{tradeId} , pre_trader=#{tradeName}


    SET live_trader_id=#{tradeId} , live_trader=#{tradeName}

WHERE match_info_id=#{matchId}

mapper.select:
SELECT
i.id,
i.match_status
FROM
rcs_standard_sport_market_sell s
LEFT JOIN standard_match_info i ON s.match_info_id = i.id
WHERE
i.sport_id=1
and i.begin_time <#{endTime} AND i.begin_time >#{startTime}

    AND i.match_status = #{matchType}


    AND i.match_status in(0,1)


    AND i.standard_tournament_id IN

        #{item,jdbcType=BIGINT}



    AND ( s.pre_trader_id IN

        #{item,jdbcType=BIGINT}

    OR
     s.live_trader_id IN

        #{item,jdbcType=BIGINT}

     )

ORDER BY i.begin_time

SELECT s.first_num,s.second_num FROM `match_event_info` s WHERE 1=1

    AND s.sport_id=#{sportId,jdbcType=BIGINT}


    AND s.standard_Match_id=#{standardMatchId,jdbcType=BIGINT}

AND s.modify_time=(SELECT max(s1.modify_time) from `match_event_info` s1
where s1.sport_id=#{sportId,jdbcType=BIGINT}
and s1.standard_match_id=#{standardMatchId,jdbcType=BIGINT}
)
limit 1
